import React, { useEffect, useState } from 'react'

const Register = () => {

  const [data, setdata] = useState({
    email:'',
    password:'',
    fullname:'',
    dateOfBirth:'',
    gender:'',
    country:[],
    recieveNewsLetters:''
  })
  useEffect(()=>{
    document.title="Register Ecommerce"
  },[])
  return (
   <div className="row">
    <div className="col-lg-6 col-lg-7">
      <div className="card">
        <div className="card-header border-bottom border-primary ">
<h2 className='text-center text-primary' style={{fontSize:'40px'}}>
  Register Yourself
</h2>
        </div>
        <div className="card-body border-bottom border-success">
{/* Email start */}
<div className="form-group form-row">
  <label htmlFor="" className='col-lg-4'>E-Mail</label>
{/* do this */}
{/* <input type="text" name="email" onChange={(e)=>{setdata({...data,email:e.target.value})}} value={data.email} className='form-control' id="" /> */}

{/* or this */}
<div className="col-lg-6">
<input type="text" id='' className='form-control' value={data.email} name='email' onChange={(e)=>{setdata({...data,[e.target.value]:e.target.value})}} />

</div>
</div>

          {/* Email end */}
   {/* password end */}
          <div className="form-group form-row">
  <label htmlFor="" className='col-lg-4'>Password</label>
{/* do this */}
{/* <input type="text" name="password" onChange={(e)=>{setdata({...data,password:e.target.value})}} value={data.password} className='form-control' id="" /> */}

{/* or this */}
<div className="col-lg-6">
<input type="text" id='' className='form-control' value={data.password} name='password' onChange={(e)=>{setdata({...data,[e.target.value]:e.target.value})}} />

</div>
</div>

          {/* password end */}

        </div>
      </div>
    </div>
   </div>
  )
}

export default Register
