export const  OrdersService = {
  getPreviousOrder:(orders)=>{
    return  orders.filter((ord)=>{
return ord.isPaymentCompleted === true
    })
  },
  getCart:(orders)=>{
    return  orders.filter((ord)=>{
return ord.isPaymentCompleted === false
    })
  }
}
export const ProductsService = {
  getProductByProductId:(products,ProductId)=>{
    return products.find((prod)=>{
return prod.id===ProductId
    })
  },
  fetchProducts :()=>{
    return fetch('http://localhost:5000/products',{method:'GET'})
  }}
