import React from 'react'
import { NavLink, useRouteError } from 'react-router-dom'
import {uc} from './UserContext'
import { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import './Nav.css';
function Navbar() {
   const ucReceive = useContext(uc)
   let a = useNavigate()
   let ur = useContext(uc)
   console.log(ur);
const onLogout=(e)=>{
  e.preventDefault()
  
  ur.setuser({
    ...ur.user,
    isLoggedIn:true,
    currentUserName: null,
    currentUserId: null
  })
a('/dashboard');
// console.log("");
}
  return (
    <div>
    <nav class="navbar navbar-expand-lg bg-warning nacbar-style"> 
  <div class="container-fluid"> 
    <a class="navbar-brand" href="/">ECOMMERCE</a> 
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation"> 
      <span class="navbar-toggler-icon"></span> 
    </button> 
    <div class="collapse navbar-collapse" id="navbarSupportedContent"> 
      <ul class="navbar-nav me-auto mb-2 mb-lg-0"> 
        
        {ur.user.isLoggedIn===true ?(
        <li class="nav-item"> 
        <NavLink class="nav-link active" aria-current="page" to='/dashboard'>DASHBOARD</NavLink> 
      </li> ):('')}
      { 
ur.user.isLoggedIn===true ?( 
               <li class="nav-item"> 
               <NavLink 
                 class="nav-link active" 
                 aria-current="page" 
                 to="/store" 
               > 
               <i class="fa fa-shopping-cart" aria-hidden="true"></i> 
                 STORE 
               </NavLink> 
             </li> 
             ):("") 
            }
       
      {!ur.user.isLoggedIn?( <li class="nav-item"> 
          <NavLink class="nav-link" aria-current="page" to='/register'>Register</NavLink> 
        </li> ):("")}

{!ur.user.isLoggedIn?( <li class="nav-item"> 
          <NavLink class="nav-link" to="/">LOGIN</NavLink> 
        </li> ):('')}


       
        <li  class="nav-item dropdown ">
          <NavLink class="nav-link dropdown-toggle" to="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
          <i className='fa fa-user-circle'></i> {ur.user.currentUserName}
          </NavLink>
          
          <ul class="dropdown-menu">
            <li><a class="dropdown-item" onClick={onLogout} href="/#">Logout</a></li>
           
          </ul>
        </li>
      </ul> 
    </div> 
  </div> 
</nav>
    </div>
  )
}

export default Navbar;
