import React, { useContext } from 'react'
import  './Navbar.css'
import {NavLink} from 'react-router-dom'
import { uc } from './UserContext'
const Navbar = () => {
  let ur = useContext(uc)

const logouthandler =()=>{
  ur.setUser({
    ...uc.user, isLoggedIn: false,
    currentUserName: null,
    currentUserId:null,
    currentUserRole:null
  })
}

  return (
<ul class='navbar'>

{
  ur.user.isLoggedIn && ur.user.currentUserRole? (
    <li><NavLink to="/dashboard">Dashboard</NavLink></li>
  ):('')
}

{
  ur.user.isLoggedIn ? (
    <li><NavLink to="/store">Store</NavLink></li>
  ):('')
}

{
  !ur.user.isLoggedIn ?(
    <li><NavLink to="/">Login</NavLink></li>
  ):('')
}

 {
  !ur.user.isLoggedIn ? (
    <li><NavLink to="/register">Register</NavLink></li>
  ):('')
 }

  <li className="user">{ur.user.currentUserName}
  
{
  ur.user.isLoggedIn ? (
    <NavLink class="lo" to="#" onClick={logouthandler}>LogOut</NavLink>
  ):('')
}

  </li>
</ul>
  )
}

export default Navbar
