import React,{useState, useEffect} from 'react'

const Login = () => {
  var [email, setEmail] = useState("");
  var [password, setPassword] = useState("");


  let [errors, setErrors] = useState( {email: [], password: []} );
  
  let [dirty, setDirty] = useState({
    email: false,
    password: false,
  });

 //executes only once - on initial render =  componentDidMount
 useEffect(() => {
  document.title = "Login - eCommerce";
}, []);

// a function to validate email and password
let validate = ()=>{
  //variable to store errorsData
  let errorsData = {};

//email
errorsData.email = [];
// condition 1
if(!email){
  errorsData.email.push("Email can't be blank")
}
// condition 2
let validEmailRegex = /\w+([-+.']\w+)*@\w+([-.]\w+)*\.\w+([-.]\w+)*/;
if(email){
if(!validEmailRegex.test(email)){
  errorsData.email.push("Proper email address is expected")
}
}
//password
errorsData.password = [];

//password can't blank
if (!password) {
  errorsData.password.push("Password can't be blank");
}
// condition2
let validPasswordRegex = /((?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,15})/;
if (password) {
  if (!validPasswordRegex.test(password)) {
    errorsData.password.push(
      "Password should be 6 to 15 characters long with at least one uppercase letter, one lowercase letter and one digit"
    );
  }
}
setErrors(errorsData)

}

useEffect(validate, [email, password]);

let isValid=()=>{
  let valid=true
   //reading all controls from errors
   for (let control in errors) {
    if (errors[control].length > 0) valid = false;
  }

  return valid;
}

  return (
    <div className='row'>
      <div className="col-lg-5 col-md-7 mx-auto">
        <div className="card border-primary shadow-lg my-2">
          <div className="card-header border-bottom border-success">
          <h4
              style={{ fontSize: "40px" }}
              className="text-primary text-center"
            >
              Login
            </h4>

          </div>
       
          <div className="card-body border-bottom border-success">
               {/* col for email */}
            <div className="form-group">
            <label htmlFor="email">Email</label>
            <input 
            type='text' 
            className="form-control"
            id="email"
            name="email"
            placeholder="Email"
            value={email}
            onChange={(e)=>{
              setEmail(e.target.value)
            }}
            />
            </div>
               {/* email ends  */}
               <div className="form-group">
            <label htmlFor="password">Password</label>
            <input 
            type='password' 
            className="form-control"
            id="password"
            name="password"
            placeholder="Password"
            value={password}
            onChange={(e)=>{
              setPassword(e.target.value)
            }}
            />
            </div>

          </div>

          <div className="card-footer text-center">
            <div className="m-1">Message</div>
            <button className="btn btn-success m-2" >
              Login
            </button>
          </div>

        </div>
      </div>
    </div>
  )
}

export default Login
