import React, { useContext, useEffect, useState } from 'react'
import { uc } from './UserContext'
import Order from './Order'

//  get previous order
let getPreviousOrders=(orders)=>{
  return orders.filter((ord)=>{
    return ord.isPaymentCompleted === true
  })
}
// get cart 
 
let getCart=(orders)=>{
  return orders.filter((ord)=>{
    return ord.isPaymentCompleted===false
  })
}

function Dashboard() {

const [orders,setOrders]=useState([])

// get context
let ur = useContext(uc)

 //executes only once - on initial render =  componentDidMount
  useEffect(()=>{
    document.title = 'DASHBOARD - E Commerce';
(async ()=>{
  let ordersResponse = await fetch(
    ` http://localhost:5000/orders?userid=${ur.user.currentUserId}`,
     {method : "GET"}
   );
   if(ordersResponse.ok){
     //satatus code is 200
     let ordersResponseBody = await ordersResponse.json();
     //get all data from products
     let productsResponse = await fetch(
      ` http://localhost:5000/orders?userid=${ur.user.currentUserId}`,
       {method : "GET"}
     );
     
     let productsResponseBody = await productsResponse.json();

// read all orders

ordersResponseBody.forEach(orders => {
  orders.product=productsResponseBody.find(
    (prod)=>{
      return prod.id===orders.productId
    }
  )
});
setOrders(ordersResponseBody)
   }
} )()


  },[ur.user.currentUserId])

 
  return (
    <div className='row'>
      <div className="col-12 py-3 header">
<h4><i className="fa fa-dashboard"></i>DASHBOARD</h4>
      </div>
    
<div className="col-12">
  <div className="row">
    {/* pervious order start */}
    <div className="col-lg-6">
      <h4 className='py-2 my-2 text-info border-bottom border-info'>
<i className="fa fa-history"></i>
PREVIOUS ORDERS
<span className="badge badge-info text-primary">{getPreviousOrders(orders).length}</span>
      </h4>

{
  getPreviousOrders(orders).length === 0 ? (
    <div className="text-danger">No orders</div>
  ):('')
}


    </div>
{/* cart start */}
<div className="col-lg-6">
  <h4 className="py-2  my-2 text-primary  border-bottom border-primary">
  <i className="fa fa-shopping-cart"></i>
CART
<span className="badge badge-info text-primary">{getCart(orders).length}</span>

  </h4>
</div>
{/* cart ends */}
  </div>
</div>

    </div>
  )
}

export default Dashboard
