import React, { useContext, useEffect, useState } from 'react' 
import { uc } from './UserContext' 
import { BrandsService, CategoriesService} from './Service' 
const Store = () => { 
    let ur = useContext(uc) 
    let [brands, setBrands] = useState([]); 
    let [categories,setCategories]=useState([]);

console.log(brands) 
useEffect(()=>{ 
    (async()=>{ 
        //brand data from db 
 
        let brandsResponse = await BrandsService.fetchBrands(); 
let brandsResponseBody = await brandsResponse.json() 
    brandsResponseBody.forEach((brand)=>{ 
        brand.isChecked = true; 
    }) 
setBrands(brandsResponseBody) 
 
  //get categories from db 
  let categoriesResponse = await CategoriesService.fetchCategories(); 
  let categoriesResponseBody = await categoriesResponse.json(); 
  categoriesResponseBody.forEach((category) => { 
    category.isChecked = true; 
  }); 
  setCategories(categoriesResponseBody); 
 
 
    })() 
}) 
 
  return ( 
   <div className="row header">
    <div className="col-lg-3">
    <h4>
            <i className="fa fa-shopping-bag"></i> Store{" "}

          </h4>
    </div>
    <div className="row">
      <div className="col-lg-3">
        <div className="my-2">
          <h5>BRANDS</h5>
          <ul className="list-group list-group-flush">
              {brands.map((brand) => (
                <li className="list-group-item" key={brand.id}>
                  <div className="form-check">
                    <input
                      type="checkbox"
                      className="form-check-input"
                      value="true"
                      checked={brand.isChecked}
                      
                      id={`brand${brand.id}`}
                    />
                    <label
                      className="form-check-label"
                      htmlFor={`brand${brand.id}`}
                    >
                      {brand.brandName}
                    </label>
                  </div>
                </li>
              ))}
            </ul>
        </div>

{/* Categories section */}
<div className="my-2">
          <h5>Categories</h5>
          <ul className="list-group list-group-flush">
              {categories.map((brand) => (
                <li className="list-group-item" key={brand.id}>
                  <div className="form-check">
                    <input
                      type="checkbox"
                      className="form-check-input"
                      value="true"
                      checked={brand.isChecked}
                      
                      id={`brand${brand.id}`}
                    />
                    <label
                      className="form-check-label"
                      htmlFor={`brand${brand.id}`}
                    >
                      {brand.brandName}
                    </label>
                  </div>
                </li>
              ))}
            </ul>
        </div>


      </div>
    </div>
   </div>
  ) 
} 
 
export default Store
