import React, { useEffect, useState } from 'react'
import Product from './Product'
import { ProductsService } from './Service'

function ProductList() {

    const [search,setSearch] = useState("")
    const [products,setProducts]= useState([])

useEffect(()=>{

(async()=>{
  let productsResponse  =await ProductsService.fetchProducts()
  let productsResponseBody = await productsResponse.json()
  setProducts(productsResponseBody)
})()

},[])



  return (
   <div className="row">
    <div className="col-12">
       <div className="row p-3 header">
        <div className="col-lg-3">
           <h4>
            <i className="fa fa-suitcase"></i>&nbsp;
            Products &nbsp;
            <span className='badge badge-secondary text-dark'>{products.length}</span>
           </h4>
        </div>
        <div className="col-lg-9">
            <input type="text" placeholder='search' value={search} onChange={(e)=>{setSearch(e.target.value)}} className='form-control' />
        </div>
       </div>
    </div>

    <div className="col-lg-10 mx-auto mb-2">
       <div className="card my-2 shadow">
        <div className="card-body">
            <table className="table">
                th
            </table>
        </div>
       </div>
    </div>
   </div>
  )
}

export default ProductList
