import React,{useEffect, useState} from "react";

const Register = () => {

  let [state, setState] = useState({
    email: "",
    password: "",
    fullname: "",
    dateOfBirth: "",
    gender: "",
    country: "",
    receiveNewsLetters: "",
  });

  let [countries] = useState([
    { id: 1, countryName: "India" },
    { id: 2, countryName: "USA" },
    { id: 3, countryName: "UK" },
    { id: 4, countryName: "Japan" },
    { id: 5, countryName: "France" },
    { id: 6, countryName: "Brazil" },
    { id: 7, countryName: "Mexico" },
    { id: 8, countryName: "Canada" }
  ]);

  let [errors, setErrors] = useState({
    email: [],
    password: [],
    fullName: [],
    dateOfBirth: [],
    gender: [],
    country: [],
    receiveNewsLetters: []
  });

  let [dirty, setDirty] = useState({
    email: false,
    password: false,
    fullName: false,
    dateOfBirth: false,
    gender: false,
    country: false,
    receiveNewsLetters: false
  });

let [message, setMessage] = useState("");

  // validation 
const validate = () =>{
let errorsData = {};
 
  // email
errorsData.email = [];
if(!state.email){
  errorsData.email.push("Email can't be blank");
}

  // password
errorsData.password = [];
if(!state.password)
{
  errorsData.password.push("Password can't be blank");
}
const validPasswordRegex = /((?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,15})/;
if(state.password){
  if(!validPasswordRegex.test(state.password)){
    errorsData.password.push(  "Password should be 6 to 15 characters long with at least one uppercase letter, one lowercase letter and one digit")
  }
}

  // fullname
errorsData.fullname=[];
if(!state.fullname){
  errorsData.fullname.push("Full Name can't be blank");
}

  // Gender
errorsData.gender=[];
if(!state.gender){
  errorsData.gender.push("Gender can't be blank");
}

  // dob
errorsData.dob=[];
if(!state.dob){
  errorsData.dob.push("Date of Birth can't be blank");
}

  // country
errorsData.country=[];
if(!state.country){
  errorsData.country.push("Country can't be blank");
}

  // newsletters
errorsData.receiveNewsLetters=[];
if(!state.receiveNewsLetters){
  errorsData.receiveNewsLetters.push("NewsLetters can't be blank");
}

}

useEffect(
  validate,[state]
)
  //executes only once - on initial render =  componentDidMount
useEffect(() => {
   document.title = "Register - eCommerce";
  }, []);
let isValid=()=>{
let valid = true;
for(let control in errors){
  if(errors[control].length > 0){
    valid = false
    
  }
}
}
  return (
    <div className="row">
      <div className="col-lg-6 col-md-7 mx-auto">
        <div className="card border-primary shadow my-2">
          <div className="card-header border-bottom border-primary">
            <h4 className="text-primary text-center">Register</h4>
          </div>

          <div className="card-body border-bottom">

            {/* email start */}
            <div className="form-group form-row">
              <label htmlFor="email" className="col-lg-4">
                E-Mail
              </label>
              <div className="col-lg-8">
                <input
                  type="text"
                  className="form-control"
                  name="email"
                  id="email"
                  value = {state.email}
                  onChange={(event) => {
                    setState({
                      ...state,
                      [event.target.name]: event.target.value,
                    });
                  }}
                />
              </div>
            </div>

            {/* password */}
            <div className="form-group form-row">
              <label htmlFor="password" className="col-lg-4">
                Password
              </label>
              <div className="col-lg-8">
                <input
                  type="password"
                  className="form-control"
                  name="password"
                  id="password"
                  value={state.password}
                  onChange={(event) => {
                    setState({
                      ...state,
                      [event.target.name]: event.target.value,
                    });
                  }}
                />
              </div>
            </div>

          {/* Fullname */}
            <div className="form-group form-row">
              <label htmlFor="fullname" className="col-lg-4">
                Full Name
              </label>
              <div className="col-lg-8">
                <input
                  type="text"
                  className="form-control"
                  name="fullname"
                  id="fullname"
                  value={state.fullname}
                  onChange={(event) => {
                    setState({
                      ...state,
                      [event.target.name]: event.target.value,
                    });
                  }}
                />
              </div>
            </div>

          {/* date of birth */}
            <div className="form-group form-row">
              <label className="col-lg-4" htmlFor="dateOfBirth">
                Date of Birth
              </label>
              <div className="col-lg-8">
                <input
                  type="date"
                  className="form-control"
                  name="dateOfBirth"
                  id="dateOfBirth"
                  value={state.dateOfBirth}
                  onChange={(event) => {
                    setState({
                      ...state,
                      [event.target.name]: event.target.value,
                    });
                  }}             
                />
              </div>
            </div>

            {/* Gender */}
            <div className="form-group form-row">
              <label className="col-lg-4">Gender</label>
              <div className="col-lg-8">
                {/* male */}
                <div className="form-check">
                  <input
                    type="radio"
                    className="form-check-input"
                    name="gender"
                    id="male"
                    value="male"
                    checked={state.gender==="male" ? true:false}
                    onChange={(event) => {
                      setState({
                        ...state,
                        [event.target.name]: event.target.value,
                      });
                    }}
                  />
                  <label className="form-check-inline" htmlFor="male">
                    Male
                  </label>
                </div>

                {/* female */}
                <div className="form-check">
                  <input
                    type="radio"
                    className="form-check-input"
                    name="gender"
                    id="female"
                    value="female"
                    checked={state.gender==="female" ? true:false}
                    onChange={(event) => {
                      setState({
                        ...state,
                        [event.target.name]: event.target.value,
                      });
                    }}
                  />
                  <label className="form-check-inline" htmlFor="female">
                    Female
                  </label>
                </div>
              </div>
            </div>

            {/* Country */}
            <div className="form-group form-row">
              <label className="col-lg-4" htmlFor="country">
                Country
              </label>
              <div className="col-lg-8">
                <select value={state.country} onChange={(e)=>{
                  setState({...state,[e.target.name]:e.target.value})
                }} name="country" id="country" className="form-control">
                <option value="">Please Select</option>
                 {
              countries.map((country)=>{
              return  
               <option key={country.id} value={country.countryName}>{country.countryName}</option>
              })
             }
                </select>
              </div>
            </div>

            {/* Newsletter */}
            <div className="form-group form-row">
              <label className="col-lg-4"></label>
              <div className="col-lg-8">
                <div className="form-check">
                  <input
                    type="checkbox"
                    name="receiveNewsLetters"
                    value="true"
                    id="receiveNewsLetters"
                    className="form-check-input"
                    checked={state.receiveNewsLetters===true?true:false}
                    onChange={(event) => {
                      setState({
                        ...state,
                        [event.target.name]: event.target.checked,
                      });
                    }}
                  />

                  <label
                    className="form-check-inline"
                    htmlFor="receiveNewsLetters"
                  >
                    Receive News Letters
                  </label>
                </div>
              </div>
            </div>

            {/* footer starts */}
            <div className="card-footer text-center">
              <div className="m-1">HELLO </div>
              <div>
                <button className="btn btn-primary m-2">Register</button>
              </div>
            </div>
            {/* footer ends */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
