          //load data from database

let ordersResponse = await fetch(
  `http://localhost:5500/orders?userid=${ur.user.currentUserId}`,{ method: "GET" }
)

if(ordersResponse.ok){}


      })()

    },[ur.user.currentUserId])

  return (
    // <div>Dashboard</div>
    <div className="row">
    <div className="col-12 py-3 header">
  <h4>
  <i className="fa fa-dashboard"></i> Dashboard
  </h4>
</div>
<div className="col-12">
  <div className="row">         
    
     {/* previous orders starts*/}
     <div className="col-lg-6">
      <h4 className="py-2 my-2 text-info border-bottom">
      <i className="fa fa-history"></i> Previous Orders{" "} 
      <span className="badge badge-info">9</span>

      </h4>
     </div>
     {/* cart starts*/}
           <div className="col-lg-6">
      <h4 className="py-2 my-2 text-info border-bottom">
      <i className="fa fa-shopping-cart"></i> Cart{" "} 
      <span className="badge badge-info">9</span>

      </h4>
     </div>
  </div>
</div>
    </div>
  )
}

export default Dashboard
