import React, { useEffect, useState } from "react";
import { uc } from "./UserContext";
import { useContext } from "react";
import { BrandsService, CategoriesService } from "./Service";
const Store = () => {
  let ur = useContext(uc);
  const [brands, setbrands] = useState([]);
  const [categories, setcategories] = useState([]);

  useEffect( ()=>{
    (async () => {
      // get brands from db
      let brandsResponse = await BrandsService.fetchBrands();
      let brandsResponseBody =await brandsResponse.json();
      brandsResponseBody.forEach((brand) => {
        brand.isChecked = true;
      });
      setbrands(brandsResponseBody);
      // get categories from db
      let categoriesResponse = await CategoriesService.fetchCategories();
      let categoriesResponseBody =await categoriesResponse.json();
      categoriesResponseBody.forEach((category) => {
        category.isChecked = true;
      });
      setcategories(categoriesResponseBody);
    })()
  },[])

let updateBrandIsChecked = (id)=>{
let brandsData = brands.map((brd)=>{
if(brd.id===id)brd.isChecked= !brd.isChecked
return brd
})
setbrands(brandsData);

}

let updateCategoryIsChecked = (id) => {
  let categoryData = categories.map((cat) => {
    if (cat.id === id) cat.isChecked = !cat.isChecked;
    return cat;
  });

  setcategories(categoryData);

};

  return (
    <div>
      <div className="row py-3 header">
        <div className="col-lg-3">
          <h4>
            <i className="fa fa-shopping-bag"></i>Store
          </h4>
        </div>
      </div>

      <div className="row">
        <div className="py-3 ">
          <div className="col-lg-3 py-2">
      
          <div className="my-2">
            <h5>Brands</h5>
            <ul className="list-group list-group-flush">
              {brands.map((brand) => (
                <li className="list-group-item" key={brand.id}>
                  <div className="form-check">
                    <input
                      type="checkbox"
                      className="form-check-input"
                      value="true"
                   onChange={()=>{
                    updateBrandIsChecked(brd.id)
                   }}
                   id={`brand${brand.id}`}
                    />
                    <label
                      className="form-check-label"
                      htmlFor={`brand${brand.id}`}
                    >
                      {brand.brandName}
                    </label>
                  </div>
                </li>
              ))}
            </ul>
          </div>

          <div className="my-2">
            <h5>Categories</h5>
            <ul className="list-group list-group-flush">
              {categories.map((category) => (
                <li className="list-group-item" key={category.id}>
                  <div className="form-check">
                    <input
                      type="checkbox"
                      className="form-check-input"
                      value="true"
                   onChange={()=>{
                    updateCategoryIsChecked
                   }}
                     
                    />
                    <label
                      className="form-check-label"
                      htmlFor={`category${category.id}`}
                    >
                      {category.categoryName}
                    </label>
                  </div>
                </li>
              ))}
            </ul>
          </div>

          </div>
        </div>
      </div>
    </div>
  );
};

export default Store;
