import React, { useEffect, useState } from "react";
import { uc } from "./UserContext";
import { useContext } from "react";
import Order from "./Order";
import {OrdersService,ProductsService}from './Service';
const Dashboard = () => {
  let [orders, setorders] = useState([]);
console.log(orders)

  //get context
  let ur = useContext(uc);

  
  //executes only once - on initial render =  componentDidMount
  useEffect(() => {
    document.title = "Dashboard - eCommerce";

    //load data from database
    // (async()=>{})()

    (async () => {
      let ordersResponse = await fetch(
        `http://localhost:5000/orders?userid=${ur.user.currentUserId}`,
        { method: "GET" }
      );

      if (ordersResponse.ok) {
        //status code is 200
        let ordersResponseBody = await ordersResponse.json();
        //  console.log(ordersResponseBody);

        //get all data from products
        let productsResponse =await ProductsService.fetchProducts()
        if (productsResponse.ok) {
          let productsResponseBody = await productsResponse.json();
          //read all orders data
// console.log(productsResponseBody);

          ordersResponseBody.forEach((ele) => {
            ele.product = ProductsService.getProductByProductId(productsResponseBody,ele.productId)
          });
          
        }
        // console.log(ordersResponseBody);
        setorders(ordersResponseBody)
      }
    })();
  }, [ur.user.currentUserId]);

  return (
    <div className="row">
      <div className="col-12 py-3 header">
        <h4>
          <i className="fa fa-dashboard" onClick={load}></i> Dashboard
        <button className="btn btn-info ms-4">
          <i className="fa fa-refresh"></i>
          REFRESH
        </button>
        </h4>
      </div>

      <div className="row">
        {/* previous orders starts*/}
        <div className="col-lg-6">
          <h4 className="py-2 my-2 text-info border-bottom border-info">
            <i className="fa fa-history"></i> Previous Orders{" "}
            <span className="badge badge-info text-primary">{OrdersService.getPreviousOrder(orders).length}</span>
          </h4>
          {OrdersService.getPreviousOrder(orders).length===0?(
            <div className="text-danger">No orders</div>
          ):('')}
          {
           OrdersService.getPreviousOrder(orders).map((ord)=>{
              return <Order
               key={ord.id} 
              orderId={ord.id} 
              productId={ord.productId} 
              userId={ord.userId} 
              isPaymentCompleted={ord.isPaymentCompleted} 
              quantity={ord.quantity} 
              productName={ord.product.productName} 
              price={ord.product.price}/>
            })
          }
        </div>
        {/* previous orders ends*/}

        {/* cart starts*/}
        <div className="col-lg-6">
          <h4 className="py-2 my-2 text-primary border-bottom border-primary">
            <i className="fa fa-shopping-cart"></i> Cart{" "}
            <span className="badge badge-primary text-primary">{OrdersService.getPreviousOrder(orders).length}</span>
          </h4>
          {OrdersService.getCart(orders).length===0?(
            <div className="text-danger">No product in your cart</div>
          ):('')}
          {
            OrdersService.getCart(orders).map((ord)=>{
              return <Order
               key={ord.id} 
              orderId={ord.id} 
              productId={ord.productId} 
              userId={ord.userId} 
              isPaymentCompleted={ord.isPaymentCompleted} 
              quantity={ord.quantity} 
              productName={ord.product.productName} 
              price={ord.product.price}/>
            })
          }
        </div>
        {/* cart ends*/}
      </div>
    </div>
  );
};

export default Dashboard;
