const Register = () => {
  const [data, setdata] = useState({
    email: "",
    password: "",
    fullname: "",
    dateOfBirth: "",
    gender: "",
    country: [],
    recieveNewsLetters: "",
  });
  const [countries, setcountries] = useState([
    { id: 0, countryname: "India" },
    { id: 0, countryname: "French" },
    { id: 0, countryname: "Bhutan" },
    { id: 0, countryname: "Germany" },
  ]);

  const [errors, seterrors] = useState({
    email: [],
    password: [],
    fullname: [],
    dateOfBirth: [],
    gender: [],
    country: [],
    recieveNewsletters: [],
  });

  const [dirty, setdirty] = useState({
    email: false,
    password: false,
    fullname: false,
    dateOfBirth: false,
    gender: false,
    country: false,
    recieveNewsletters: false,
  });
  // const yourcountry = countries.map((countryname,id)=>{
  //   return <option key = {id} value='country'>{countryname}</option>
  // })

  const [message, setmessage] = useState("");

  // validate
  const validate = () => {
    const errorData = [];
    // email
    errorsData.email = [];
    //email cant be blank

    if (!data.email) {
      errorData.email.push("email cant be blank");
    }
    //email regex

    const validEmailRegex = /\w+([-+.']\w+)*@\w+([-.]\w+)*\.\w+([-.]\w+)*/;
    if (data.email) {
      if (!validEmailRegex.test(data.email)) {
        errorData.email.push("Proper email address is expected");
      }
    }
    //password
    errorsData.password = [];
    //password cant be blank

    if (!data.password) {
      errorData.password.push("password cant be blank");
    }
    //password regex

    const validpasswordRegex = /((?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,15})/;
    if (data.password) {
      if (!validpasswordRegex.test(data.password)) {
        errorData.password.push("Proper password is expected");
      }
    }
    //fullName
    errorsData.fullName = [];

    //fullName can't blank
    if (!data.fullName) {
      errorsData.fullName.push("Full Name can't be blank");
    }

    //gender
    errorsData.gender = [];

    //gender can't blank
    if (!data.gender) {
      errorsData.gender.push("Gender can't be blank");
    }

    //country
    errorsData.country = [];

    //country can't blank
    if (!data.country) {
      errorsData.country.push("Country can't be blank");
    }

    //country
    errorsData.recieveNewsLetters = [];

    seterrors(errorData);
  };

  useEffect(validate, [data]);

  useEffect(() => {
    document.title = "register";
  }, []);

const onRegisterClick = () =>{
  let dirtyData = dirty;
