    dirtyData[control] = true
  })
  setdirty(dirtyData)
}


  return (
    <div className="row">
      <div className="col-md-7 col-lg-6 mx-auto">
        <div className="card border-primary my-3">
          <div className="card-header border-bottom border-primary">
            <h3 className="text-center text-primary "> Register YourSelf</h3>
            <ul>{Object.keys(errors).map((control)=>{
              if(dirty[control]){
                return errors[control].map((err)=>{
                  return <li className="text-danger" key={err}>{err}</li>
                })
              }
            })}</ul>
          </div>
          <div className="card-body border-bottom border-success">
            {/* Email start */}
            <div className="from-group form-row">
              <label htmlFor="email" className="col-lg-4">
                E-mail
              </label>
              <input
                className="form-control"
                type="text"
                name="email"
                id="email"
                value={data.email}
                onChange={(e) => {
                  setdata({ ...data, [e.target.name]: e.target.value });
                }}
              />
              {/* Email end */}

              {/* password start */}
              <div className="from-group form-row">
                <label htmlFor="password" className="col-lg-4">
                  Password
                </label>
                <input
                  type="text"
                  name="password"
                  id="password"
                  className="form-control"
                  value={data.password}
                  onChange={(e) => {
                    setdata({ ...data, [e.target.name]: e.target.value });
                  }}
                />
              </div>
              {/* password end */}

              {/* fullname start */}
              <div className="from-group form-row">
                <label htmlFor="fullname" className="col-lg-4">
                  FullName
                </label>
                <input
                  type="text"
                  name="fullname"
                  id="fullname"
                  className="form-control"
                  value={data.fullname}
                  onChange={(e) => {
                    setdata({ ...data, [e.target.name]: e.target.value });
                  }}
                />
              </div>
              {/* fullname end */}
