              {/* newsletter start */}

              <div className="form-group form-row">
                <label htmlFor="newsletter" className="col-lg-4">
                  Newsletter
                </label>

                <div className="col-lg-6">
                  <div className="form-check">
                    <input
                      type="checkbox"
                      name="recieveNewsLetters"
                      id="recieveNewsLetters"
                      className="form-check-input"
                      value="true"
                      checked={data.recieveNewsLetters === true ? true : false}
                      onChange={(e) => {
                        setdata({ ...data, [e.target.name]: e.target.checked });
                      }}
                    />
                    <label htmlFor="newsletters" className="form-check-inline">
                      Recieve
                    </label>
                  </div>
                </div>
              </div>

              {/* newsletter end */}
{/* footer start */}
<div className="card-footer text-center">
  <div className="m-1">
    {message}
  </div>
</div>
<button onClick={onRegisterClick} className="btn btn-primary">Register</button>

            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

