import React, { useState, useEffect } from "react";

const Register = () => {
  let [state, setState] = useState({
    email: "",
    password: "",
    fullName: "",
    dateOfbirth: "",
    gender: "",
    country: "",
    receiveNewsLetters: "",
  });

  let [countries] = useState([
    { id: 1, countryName: "India" },
    { id: 2, countryName: "USA" },
    { id: 3, countryName: "UK" },
    { id: 4, countryName: "Japan" },
    { id: 5, countryName: "France" },
    { id: 6, countryName: "Brazil" },
    { id: 7, countryName: "Mexico" },
    { id: 8, countryName: "Canada" },
  ]);

  let [errors, setErrors] = useState({
    email: [],
    password: [],
    fullName: [],
    dateOfbirth: [],
    gender: [],
    country: [],
    receiveNewsLetters: [],
  });

  let [dirty, setDirty] = useState({
    email: false,
    password: false,
    fullName: false,
    dateOfbirth: false,
    gender: false,
    country: false,
    receiveNewsLetters: false,
  });

  let [message, setMessage] = useState("");

  //executes only once - on initial render =  componentDidMount
  useEffect(() => {
    document.title = "Register - eCommerce";
  }, []);

  const validate = () => {
    let errorsData = {};


    
  };

  return (
    <div className="row">
      <div className="col-lg-6 col-md-7 mx-auto">
        <div className="card-border-primary">
          <div className="card-header border-bottom border-primary">
            <h4 className="text-primary text-center">Register</h4>
          </div>
          <div className="card-body border-bottom">
            <div className="form-group form-row">
              <label htmlFor="email" className="col-lg-4">
                Email
              </label>
              <div className="col-lg-8">
                <input
                  type="text"
                  name="email"
                  className="form-control"
                  id="email"
                  value = {state.email}
                  onChange = {(e)=>{setState({...state,[e.target.name]:e.target.value})}}
                />
              </div>
            </div>

            <div className="form-group form-row">
              <label htmlFor="password" className="col-lg-4">
                Password
              </label>
              <div className="col-lg-8">
                <input
                  type="password"
                  name="password"
                  className="form-control"
                  id="password"
                  value = {state.password}
                  onChange = {(e)=>{setState({...state,[e.target.name]:e.target.value})}}
                />
              </div>
            </div>

                {/* fullName starts */}
                <div className="form-group form-row">
              <label className="col-lg-4" htmlFor="fullName">
                Full Name
              </label>
              <div className="col-lg-8">
                <input
                  type="text"
                  className="form-control"
                  name="fullName"
                  id="fullName"
                  value={state.fullName}
                  onChange={(event) => {
                    setState({
                      ...state,
                      [event.target.name]: event.target.value,
                    });
                  }}
                  onBlur={(event) => {
                    setDirty({ ...dirty, [event.target.name]: true });
                    validate();
                  }}
                />

               
              </div>
            </div>
            {/* fullName ends */}

            <div className="form-group form-row">
              <label htmlFor="dateOfbirth" className="col-lg-4">
                Date Of Birth
              </label>
              <div className="col-lg-8">
                <input
                  type="date"
                  name="dateOfbirth"
                  className="form-control"
                  id="dateOfbirth"
                  value = {state.dateOfbirth}
                  onChange = {(e)=>{setState({...state,[e.target.name]:e.target.value})}}
                />
              </div>
            </div>
            {/* gender starts */}
            <div className="form-group form-row">
              <label className="col-lg-4">Gender</label>
              <div className="col-lg-8">
                <div className="form-check">
                  <input
                    type="radio"
                    name="gender"
                    value="male"
                    id="male"
                    className="form-check-input"
                    checked={state.gender==='male' ? true:false}
                    onChange = {(e)=>{setState({...state,[e.target.name]:e.target.value})}}

                  />

                  <label className="form-check-inline" htmlFor="male">
                    Male
                  </label>
                </div>

                <div className="form-check">
                  <input
                    type="radio"
                    name="gender"
                    value="female"
                    id="female"
                    className="form-check-input"
                    checked={state.gender==='female' ? true:false}
                    onChange = {(e)=>{setState({...state,[e.target.name]:e.target.value})}}

                  />

                  <label className="form-check-inline" htmlFor="female">
                    Female
                  </label>
                </div>
              </div>
            </div>
            {/* gender ends */}
            {/* country starts */}
            <div className="form-group form-row">
              <label className="col-lg-4" htmlFor="country">
                Country
              </label>
              <div className="col-lg-8">
                <select
                  className="form-control"
                  name="country"
                  id="country"
                  value={state.country}
                  onChange={(e) => {
                    setState({ ...state, [e.target.name]: e.target.value });
                    
                  }}
                >
                  <option value="">Please Select</option>
                  {countries.map((ele) => {
                    return (
                      <option key={ele.id} value={ele.id}>
                        {ele.countryName}
                      </option>
                    );
                  })}
                </select>
              </div>
            </div>
            {/* country ends */}

            {/* receiveNewsLetters starts */}
            <div className="form-group form-row">
              <label className="col-lg-4"></label>
              <div className="col-lg-8">
                <div className="form-check">
                  <input
                    type="checkbox"
                    name="receiveNewsLetters"
                    value="true"
                    id="receiveNewsLetters"
                    className="form-check-input"
                    checked={state.receiveNewsLetters===true ? true:false}
                    onChange={(e) => {
                      setState({ ...state, [e.target.name]: e.target.checked});
                      
                    }}
                  />

                  <label
                    className="form-check-inline"
                    htmlFor="receiveNewsLetters"
                  >
                    Receive News Letters
                  </label>
                </div>
              </div>
            </div>
            {/* receiveNewsLetters ends */}
          </div>
          {/* footer starts */}
          <div className="card-footer text-center">
            <div className="m-1">MESSAGE</div>
            <div>
              <button className="btn btn-primary m-2">Register</button>
            </div>
          </div>
          {/* footer ends */}
        </div>
      </div>
    </div>
  );
};

export default Register;
