import React, { useContext, useEffect, useState } from 'react'
import {uc} from './UserContext'

const Dashborad = () => {
let [orders,setorders] =  useState([])
// get context
let ur = useContext(uc)
// getPreviousOrders
  const getPreviousOrders=()=>{
    return orders.filter((ele)=>{
return ele.isPaymentCompleted===true
    })
  }
  //getCart
  const getCart=(orders)=>{
    return orders.filter((ord)=>{
return ord.isPaymentCompleted===false
    })
  }
  //execute only once - on initial render = componentDidMount
  useEffect(()=>{
    document.title = "Dashboard - e commerce";
  },[])
useEffect(()=>{

  //load data from database
// (async()=>{})()

(async()=>{
  let ordersResponse = await fetch(`http://localhost:5000/orders?userid=${ur.user.currentUserId}`,{method:"GET"})

if(ordersResponse.ok){
  //status code is 200
  let ordersResponseBody = await ordersResponse.json()
  // console.log(ordersResponseBody);
  // get all data from product

  let productsResponse = ("https://localhost:5000/products",{
    method:"GET",
  });
  if(productsResponse.ok){
let productsResponseBody = await productsResponse.json()
// read all orders data
ordersResponseBody.forEach
  }
}


})()
},[])

  return (
    <div className='row'>
      <div className="col-12 py-3 header">
        <h4>
        <i className="fa fa-dashboard"></i> Dashboard
        </h4>
      </div>

      <div className="row">
          {/* previous orders starts*/}
          <div className="col-lg-6">
            <h4 className="py-2 my-2 text-info border-bottom border-info">
              <i className="fa fa-history"></i> Previous Orders{" "}
              <span className="badge badge-info">
               2
              </span>
            </h4>


       
          </div>
          {/* previous orders ends*/}

          {/* cart starts*/}
          <div className="col-lg-6">
            <h4 className="py-2 my-2 text-primary border-bottom border-primary">
              <i className="fa fa-shopping-cart"></i> Cart{" "}
              <span className="badge badge-primary">
           5
              </span>
            </h4>
          </div>
          {/* cart ends*/}
        </div>
      </div>

   
  )
}

export default Dashborad
