import React, { useContext } from 'react'
import { NavLink } from 'react-router-dom'
import './Nav.css'
import {uc} from './UserContext'
import { useNavigate } from 'react-router-dom'
const Navbar = () => {
  let ur = useContext(uc)
  let nav = useNavigate()

  // get context
  let onLogoutClick = (e) => {
    e.preventDefault()

    ur.setuser({isLoggedIn: false,
      currentUserId: null,
      currentUserName: null})
      nav('/')
  }
  return (
    <div>
      <ul className='main'>
        <li><NavLink className='links' to="/">E-COMMERCE</NavLink></li>
{
  ur.user.isLoggedIn === true ? (
    <li><NavLink className='links' to="/dashboard">Dashboad</NavLink></li>

  ):("")
}

{
  ur.user.isLoggedIn === true ? (
        <li><NavLink className='links' to="/">Login</NavLink></li> ):("")
      }
        <li><NavLink className='links' to="/register">Register</NavLink></li>
        <li className='menu'><span style={{fontSize:'25px'}}>User</span>
          <ul > 
          <li><NavLink className='links' to="#" onClick={onLogoutClick}>Logout</NavLink></li>
          </ul>
        </li>
      </ul>
    </div>
  )
}

export default Navbar
