import React from "react";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Order(props) {
  const notify = () => toast("Item Purchased!");
  const deleteItem = ()=>toast("Item Deleted From Cart")
  // console.log("Order rendered", props);
  return (
    <div className="card my-2 shadow">
      <div className="card-body">
        <h6>
          <i className="fa fa-arrow-right"></i> {props.productName}
          {props.isPaymentCompleted === false ? (
            <div className="float-right">
              <button
                className="btn btn-sm btn-info mr-2"
                onClick={() => {
                  props.onBuyNowClick(
                    props.orderId,
                    props.userId,
                    props.productId,
                    props.quantity
                  );
                  notify()
                }}
              >
                <i className="fa fa-truck"></i> Buy Now
              </button>
              <button
                className="btn btn-sm btn-danger mr-2"
                onClick={() => {
                  props.onDeleteClick(props.orderId);
                }}
              >
                <i className="fa fa-trash-o"></i> Delete
              </button>
              <ToastContainer />
            </div>
          ) : (
            ""
          )}
        </h6>

        <table className="table table-sm table-borderless mt-1">
          <tbody>
            <tr>
              <td style={{ width: "100px" }}>Quantity:</td>
              <td>{props.quantity}</td>
            </tr>
            <tr>
              <td style={{ width: "100px" }}>Price:</td>
              <td>$ {props.price}</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default React.memo(Order);
